memberdef.initializer:
= [](const QByteArray& byteArray) {
    if (byteArray.size() < 2)
        return false;

    return static_cast<uchar>(byteArray[0]) == 0xFF && static_cast<uchar>(byteArray[1]) == 0xFE
        || static_cast<uchar>(byteArray[0]) == 0xFE && static_cast<uchar>(byteArray[1]) == 0xFF
        || (byteArray[0] == 0x00 && byteArray[1] == '{') || (byteArray[0] == '{' && byteArray[1] == 0x00);
}

= {
    { PixelSelectionType::Rectangle, "Rectangle" },
    { PixelSelectionType::Line, "Line" },
    { PixelSelectionType::Brush, "Brush" },
    { PixelSelectionType::Lasso, "Lasso" },
    { PixelSelectionType::Polygon, "Polygon" },
    { PixelSelectionType::Sample, "Sample" },
    { PixelSelectionType::ROI, "ROI" }
}
